print(DNAq9.count('T'))
print(DNAq9.count('G'))
print(DNAq9.count('A'))
print(DNAq9.count('C'))
print(f'{DNAq9ATpercentage:.0%}')
print(f'{DNAq9GCpercentage:.0%}')

