fasta_streamlined = {}
DNA = ''
with open("Python_06.fasta","r") as python6fasta:
    for line in python6fasta:
        line = line.rstrip()
        if line.startswith(">seq"):
            seqID = line
            fasta_streamlined[seqID] = ''
        else:
            fasta_streamlined[seqID] += line
print(fasta_streamlined)
